
package mintic.edu.upb.mundo;

/**
 * Clase Empleado con nombre, edad y salario
 * @author Carlos Beltrán
 */
public class Empleado {
    // Construcción de la Clase
    
    // Declaración de Atributos
    private String nombre;
    private int edad;
    private double salario;
    
    // Agregar constructores, permite crear objetos

    public Empleado() {
    }

    public Empleado(String nombre, int edad, double salario) {
        this.nombre = nombre;
        this.edad = edad;
        this.salario = salario;
    }
    
    // Agregar los métodos set y get

    public String getNombre() {
        return nombre;
    }

    public void setNombre(String nombre) {
        this.nombre = nombre;
    }

    public int getEdad() {
        return edad;
    }

    public void setEdad(int edad) {
        this.edad = edad;
    }

    public double getSalario() {
        return salario;
    }

    public void setSalario(double salario) {
        this.salario = salario;
    }
    
    // toString

    @Override
    public String toString() {
        return "Datos del Empleado" + 
                "\nNombre      : " + nombre + 
                "\nEdad        : " + edad + 
                "\nSalario     : " + salario + 
                "\nIncremento  : " + calcularIncremento() + 
                "\nSalario Neto: " + calcularSalarioNeto() + "\n";
    }
    
    // Métodos propios
    
    public double calcularIncremento(){
        double incremento = 0;
        if (this.edad >= 18 && this.edad <= 50){
            incremento = this.salario * 0.05;
        }
        if (this.edad >= 51 && this.edad <= 60){
            incremento = this.salario * 0.10;
        }
        if (this.edad > 60){
            incremento = this.salario * 0.15;
        }
        return incremento;
    }
    
    // Método si es menor de edad tipo boolean
    
    public boolean esMenorDeEdad(){
        boolean menor = true;
        if (this.edad >= 18 ) {
            menor = false;
        }
        return menor;
    }
    
    // Método para calcularSalarioNeto
    public double calcularSalarioNeto(){
        double salarioNeto = 0;
        salarioNeto = this.salario + calcularIncremento();
        return salarioNeto;
    }
            
}
