
package mintic.edu.upb.interfaz;

import java.util.Scanner;

/**
 * Ejemplos de uso de Scanner
 * @author Carlos Beltrán
 */
public class UsoConsolaApp {

    /**
     * @param args the command line arguments
     */
    public static void main(String[] args) {
        // TODO code application logic here
        Scanner leerDato = new Scanner(System.in);
        String name;
        System.out.print("Escriba el nombre de la Persona: ");
        name = leerDato.nextLine();
        char sexo;
        System.out.print("Escriba la letra que identifica el sexo según F:Femenino ó M:Masculino de la Persona: ");
        sexo = leerDato.next().charAt(0);
        int edad;
        System.out.print("Escriba la edad de la Persona: ");
        edad = leerDato.nextInt();
        long mobileNo;
        System.out.print("Escriba el número del celular la Persona: ");
        mobileNo = leerDato.nextLong();
        double promedio;
        System.out.print("Escriba el el promedio ponderado de la Persona: ");
        promedio = leerDato.nextDouble();
        System.out.println("El nombre de la Persona es: "+name);
        System.out.println("El sexo de la Persona es: "+sexo);
        System.out.println("La edad de la Persona es: "+edad+ " Años");
        System.out.println("El número del celular de la Persona es: "+mobileNo);
        System.out.println("El Promedio ponderado de la Persona es: "+promedio);
    }
}
